"use client"

import { useState } from "react"
import { useSelector } from "react-redux"
import Navbar from "./Navbar"
import Footer from "./Footer"
import { toast } from "react-toastify"
import { Loader2, BookOpen, CheckCircle, Award, ChevronDown } from "lucide-react" // Importing Lucide React icons

const MCQTest = () => {
  const [topic, setTopic] = useState("")
  const [questions, setQuestions] = useState([])
  const [correctAnswers, setCorrectAnswers] = useState([])
  const [userAnswers, setUserAnswers] = useState(new Array(5).fill(null)) // Fixed to 5 as per original logic
  const [result, setResult] = useState(null)
  const [loading, setLoading] = useState(false)
  const [submitting, setSubmitting] = useState(false)
  const userId = useSelector((state) => state.auth.userId) // Assuming userId is available in Redux

  const predefinedTopics = ["JavaScript", "Python", "React.js", "Node.js", "MongoDB", "Data Structures"]

  const fetchQuestions = async () => {
    if (!topic) {
      toast.error("Please select a topic.")
      return
    }

    setLoading(true)
    setQuestions([]) // Clear previous questions
    setUserAnswers(new Array(5).fill(null)) // Clear previous answers, fixed to 5
    setResult(null) // Clear previous results

    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/generate", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ topic }),
      })
      const data = await response.json()

      if (data.success) {
        setQuestions(data.questions)
        setCorrectAnswers(data.questions.map((q) => q.correctAnswer))
        // Note: userAnswers is intentionally kept at fixed size 5 as per original logic
        toast.success(`Generated ${data.questions.length} questions for ${topic}!`)
      } else {
        toast.error(data.message || "Failed to generate questions.")
      }
    } catch (error) {
      console.error("Error fetching questions:", error)
      toast.error("Error fetching questions. Please try again.")
    }
    setLoading(false)
  }

  const handleAnswer = (index, answer) => {
    const updatedAnswers = [...userAnswers]
    updatedAnswers[index] = answer
    setUserAnswers(updatedAnswers)
  }

  const submitTest = async () => {
    if (userAnswers.includes(null)) {
      toast.error("Please answer all questions.")
      return
    }

    setSubmitting(true)
    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/submit", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, topic, userAnswers, correctAnswers }),
      })
      const data = await response.json()

      if (data.success) {
        setResult(data)
        toast.success("Test submitted successfully!")
      } else {
        toast.error(data.message || "Failed to submit test.")
      }
    } catch (error) {
      console.error("Error submitting test:", error)
      toast.error("Error submitting test. Please try again.")
    }
    setSubmitting(false)
  }

  const resetQuiz = () => {
    setTopic("")
    setQuestions([])
    setCorrectAnswers([])
    setUserAnswers(new Array(5).fill(null)) // Reset to fixed 5 nulls
    setResult(null)
    setLoading(false)
    setSubmitting(false)
  }

  const answeredCount = userAnswers.filter((answer) => answer !== null).length
  const totalQuestions = questions.length > 0 ? questions.length : 5 // Use actual length if questions are loaded, else default to 5 for progress bar

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-blue-50 via-white to-indigo-50 dark:from-gray-900 dark:to-gray-800">
      <Navbar />

      <div className="flex-grow container mx-auto px-4 py-8 max-w-4xl">
        <div className="text-center mb-10">
          <h1 className="text-4xl sm:text-5xl font-extrabold text-gray-900 dark:text-white leading-tight">
            Knowledge <span className="text-blue-600 dark:text-blue-400">Challenge</span>
          </h1>
          <p className="mt-3 text-lg text-gray-600 dark:text-gray-300">
            Test your skills and earn badges with our interactive quizzes
          </p>
        </div>

        {/* Topic Selection Card */}
        {!questions.length && !result && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 sm:p-8 mb-8 transform transition-all hover:shadow-xl border border-gray-100 dark:border-gray-700">
            <h2 className="text-2xl font-bold mb-6 text-gray-900 dark:text-white flex items-center">
              <BookOpen className="h-7 w-7 mr-3 text-blue-500 dark:text-blue-400" />
              Select Your Topic
            </h2>
            <div className="relative mb-6">
              <select
                className="w-full p-3 sm:p-4 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-800 dark:text-white appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all text-base sm:text-lg"
                value={topic}
                onChange={(e) => setTopic(e.target.value)}
                disabled={loading}
              >
                <option value="">-- Select a Topic --</option>
                {predefinedTopics.map((t) => (
                  <option key={t} value={t}>
                    {t}
                  </option>
                ))}
              </select>
              <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-4 text-gray-700 dark:text-gray-300">
                <ChevronDown className="h-5 w-5" />
              </div>
            </div>
            <button
              onClick={fetchQuestions}
              disabled={loading || !topic}
              className={`w-full py-3 sm:py-4 rounded-lg font-semibold text-white text-lg transition-all transform hover:scale-[1.01] active:scale-[0.99] ${
                !topic
                  ? "bg-gray-400 cursor-not-allowed"
                  : loading
                    ? "bg-blue-500 dark:bg-blue-600 cursor-wait"
                    : "bg-blue-600 hover:bg-blue-700 dark:bg-blue-700 dark:hover:bg-blue-800 shadow-md hover:shadow-lg"
              }`}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <Loader2 className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" />
                  Generating Questions...
                </span>
              ) : (
                "Start Quiz"
              )}
            </button>
          </div>
        )}

        {/* Questions Section */}
        {questions.length > 0 && !result && (
          <div className="space-y-6 mb-8">
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 sm:p-8 border border-gray-100 dark:border-gray-700">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                  Quiz on <span className="text-blue-600 dark:text-blue-400">{topic}</span>
                </h2>
                <div className="text-lg font-semibold text-gray-700 dark:text-gray-300">
                  {answeredCount}/{totalQuestions} Answered
                </div>
              </div>
              <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2.5 mb-6">
                <div
                  className="bg-blue-600 dark:bg-blue-500 h-2.5 rounded-full transition-all duration-300"
                  style={{ width: `${(answeredCount / totalQuestions) * 100}%` }}
                ></div>
              </div>

              {questions.map((q, i) => (
                <div
                  key={i}
                  className="bg-gray-50 dark:bg-gray-900 rounded-xl shadow-sm p-5 mb-4 border border-gray-100 dark:border-gray-700 last:mb-0"
                >
                  <div className="flex items-start mb-4">
                    <span className="flex-shrink-0 bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-200 font-bold rounded-full w-8 h-8 flex items-center justify-center mr-3 text-sm">
                      {i + 1}
                    </span>
                    <h3 className="text-lg font-semibold text-gray-800 dark:text-white leading-relaxed">
                      {q.question}
                    </h3>
                  </div>
                  <div className="ml-11 space-y-3">
                    {q.options.map((option, idx) => (
                      <label
                        key={idx}
                        className={`flex items-center p-3 rounded-lg cursor-pointer transition-all duration-200 ${
                          userAnswers[i] === option
                            ? "bg-blue-100 dark:bg-blue-900 border-2 border-blue-500 dark:border-blue-400 text-blue-800 dark:text-blue-200 shadow-sm"
                            : "border border-gray-200 dark:border-gray-700 hover:bg-gray-100 dark:hover:bg-gray-700 text-gray-700 dark:text-gray-300"
                        }`}
                      >
                        <div
                          className={`w-5 h-5 flex-shrink-0 rounded-full border-2 ${
                            userAnswers[i] === option
                              ? "border-blue-500 dark:border-blue-400 bg-blue-500 dark:bg-blue-400"
                              : "border-gray-400 dark:border-gray-500 bg-white dark:bg-gray-800"
                          } flex items-center justify-center mr-3 transition-all`}
                        >
                          {userAnswers[i] === option && (
                            <div className="w-2.5 h-2.5 rounded-full bg-white dark:bg-gray-900"></div>
                          )}
                        </div>
                        <span className="text-base">{option}</span>
                        <input
                          type="radio"
                          name={`q${i}`}
                          value={option}
                          onChange={() => handleAnswer(i, option)}
                          checked={userAnswers[i] === option}
                          className="sr-only"
                        />
                      </label>
                    ))}
                  </div>
                </div>
              ))}
            </div>

            <button
              onClick={submitTest}
              disabled={submitting || userAnswers.includes(null)}
              className={`w-full py-4 rounded-xl text-white text-lg font-bold transition-all transform hover:scale-[1.01] active:scale-[0.99] ${
                userAnswers.includes(null)
                  ? "bg-gray-400 dark:bg-gray-600 cursor-not-allowed"
                  : submitting
                    ? "bg-blue-500 dark:bg-blue-600 cursor-wait"
                    : "bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 shadow-lg hover:shadow-xl"
              }`}
            >
              {submitting ? (
                <span className="flex items-center justify-center">
                  <Loader2 className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" />
                  Submitting...
                </span>
              ) : (
                "Submit Quiz"
              )}
            </button>
            {userAnswers.includes(null) && (
              <p className="mt-3 text-center text-sm text-gray-600 dark:text-gray-400">
                Please answer all questions ({totalQuestions - answeredCount} remaining)
              </p>
            )}
          </div>
        )}

        {/* Result Display */}
        {result && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 sm:p-8 border-2 border-purple-300 dark:border-purple-600 animate-fade-in">
            <div className="text-center">
              <div className="inline-block p-4 bg-purple-100 dark:bg-purple-900 rounded-full mb-4">
                <CheckCircle className="h-12 w-12 text-purple-600 dark:text-purple-400" />
              </div>
              <h2 className="text-2xl sm:text-3xl font-bold text-gray-900 dark:text-white mb-2">Quiz Completed!</h2>
              <div className="text-5xl sm:text-6xl font-extrabold text-purple-600 dark:text-purple-400 my-4">
                {result.correctCount}/{questions.length}
              </div>
              <p className="text-lg text-gray-600 dark:text-gray-300 mb-4">
                {result.correctCount === questions.length
                  ? "Perfect score! Excellent work!"
                  : result.correctCount >= questions.length / 2
                    ? "Good job! You're making progress."
                    : "Keep practicing to improve your score."}
              </p>
              {result.badgeEarned && (
                <div className="mt-6 p-4 bg-gradient-to-r from-yellow-100 to-amber-100 dark:from-yellow-900 dark:to-amber-900 rounded-lg inline-block">
                  <div className="flex items-center justify-center">
                    <Award className="h-8 w-8 text-amber-600 dark:text-amber-400 mr-2" />
                    <span className="text-xl font-bold text-amber-800 dark:text-amber-300">
                      Badge Earned: {result.badgeEarned}
                    </span>
                  </div>
                </div>
              )}
              <button
                onClick={resetQuiz}
                className="mt-8 px-8 py-4 bg-purple-600 hover:bg-purple-700 dark:bg-purple-700 dark:hover:bg-purple-800 text-white font-semibold rounded-xl transition-all transform hover:scale-[1.01] active:scale-[0.99] shadow-md hover:shadow-lg"
              >
                Take Another Quiz
              </button>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  )
}

export default MCQTest
