import { useState } from "react";
import { useSelector } from "react-redux";
import Navbar from "./Navbar";
import Footer from "./Footer";
import { toast } from "react-toastify";
import {
  Loader2,
  BookOpen,
  CheckCircle,
  Award,
  ChevronDown,
} from "lucide-react"; // Lucide icons
import clsx from "clsx"; // Conditional className helper

const MCQTest = () => {
  const [topic, setTopic] = useState("");
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(false);
  const [score, setScore] = useState(null);
  const userId = useSelector((state) => state.auth.user?._id);

  const handleOptionChange = (questionIndex, selectedOption) => {
    setAnswers((prev) => ({ ...prev, [questionIndex]: selectedOption }));
  };

  const fetchQuestions = async () => {
    if (!topic) {
      toast.error("Please select a topic");
      return;
    }

    try {
      setLoading(true);
      const res = await fetch(`https://mcqapi-h4x1.onrender.com/api/mcq/questions?topic=${topic}`);
      const data = await res.json();
      setQuestions(data.questions);
      setAnswers({});
      setScore(null);
    } catch (err) {
      toast.error("Failed to fetch questions");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    if (Object.keys(answers).length !== questions.length) {
      toast.error("Please answer all questions before submitting");
      return;
    }

    let calculatedScore = 0;
    questions.forEach((question, index) => {
      if (answers[index] === question.correctOption) calculatedScore++;
    });

    setScore(calculatedScore);

    try {
      await fetch("https://mcqapi-h4x1.onrender.com/api/mcq/submit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, topic, score: calculatedScore }),
      });
    } catch (err) {
      toast.error("Failed to submit score");
    }
  };

  return (
    <>
      <Navbar />
      <div className="max-w-4xl mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-4 text-center flex justify-center items-center gap-2">
          <BookOpen className="w-8 h-8 text-purple-600" />
          MCQ Quiz
        </h1>

        <div className="mb-6 flex flex-col md:flex-row items-center gap-4">
          <div className="relative w-full md:w-1/2">
            <select
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
              className="block appearance-none w-full bg-white border border-gray-300 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:ring-2 focus:ring-purple-600"
            >
              <option value="">Select a topic</option>
              <option value="React">React</option>
              <option value="JavaScript">JavaScript</option>
              <option value="HTML">HTML</option>
              <option value="CSS">CSS</option>
            </select>
            <ChevronDown className="absolute right-3 top-3.5 text-gray-500 pointer-events-none" />
          </div>
          <button
            onClick={fetchQuestions}
            disabled={!topic || loading}
            className={clsx(
              "w-full md:w-auto py-3 px-6 rounded-lg font-semibold text-white transition duration-300",
              !topic || loading
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-purple-600 hover:bg-purple-700 shadow-md"
            )}
          >
            {loading ? (
              <span className="flex items-center justify-center gap-2">
                <Loader2 className="w-5 h-5 animate-spin" /> Loading...
              </span>
            ) : (
              "Start Quiz"
            )}
          </button>
        </div>

        {questions.length > 0 && (
          <div className="space-y-6">
            {questions.map((question, index) => (
              <div key={index} className="bg-white p-6 rounded-lg shadow-md">
                <h2 className="text-lg font-medium mb-4">
                  Q{index + 1}: {question.question}
                </h2>
                <div className="space-y-2">
                  {question.options.map((option, i) => (
                    <label
                      key={i}
                      className="flex items-center space-x-2 cursor-pointer"
                    >
                      <input
                        type="radio"
                        name={`question-${index}`}
                        value={option}
                        checked={answers[index] === option}
                        onChange={() =>
                          handleOptionChange(index, option)
                        }
                        className="form-radio text-purple-600"
                      />
                      <span>{option}</span>
                    </label>
                  ))}
                </div>
              </div>
            ))}

            <div className="flex justify-center mt-8">
              <button
                onClick={handleSubmit}
                className="bg-green-600 hover:bg-green-700 text-white font-semibold py-3 px-6 rounded-lg shadow-md transition duration-300"
              >
                Submit Quiz
              </button>
            </div>
          </div>
        )}

        {score !== null && (
          <div className="mt-10 bg-white p-6 rounded-lg shadow-md text-center">
            <h2 className="text-2xl font-bold text-purple-600 mb-2 flex justify-center items-center gap-2">
              <Award className="w-6 h-6" />
              Quiz Completed!
            </h2>
            <p className="text-xl">
              You scored <span className="font-bold">{score}</span> out of{" "}
              {questions.length}
            </p>
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default MCQTest;
