"use client"

import { useState } from "react"
import { useSelector } from "react-redux"
import Navbar from "./Navbar"
import Footer from "./Footer"
import { toast } from "react-toastify"
import { Loader2, BookOpen, CheckCircle, Award, ChevronDown } from "lucide-react"

const MCQTest = () => {
  const [topic, setTopic] = useState("")
  const [questions, setQuestions] = useState([])
  const [correctAnswers, setCorrectAnswers] = useState([])
  const [userAnswers, setUserAnswers] = useState([])
  const [result, setResult] = useState(null)
  const [loading, setLoading] = useState(false)
  const [submitting, setSubmitting] = useState(false)
  const userId = useSelector((state) => state.auth.userId)

  const predefinedTopics = ["JavaScript", "Python", "React.js", "Node.js", "MongoDB", "Data Structures"]

  const fetchQuestions = async () => {
    if (!topic) {
      toast.error("Please select a topic.")
      return
    }

    setLoading(true)
    setQuestions([])
    setUserAnswers([])
    setResult(null)

    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/generate", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ topic }),
      })

      const data = await response.json()

      if (data.success) {
        setQuestions(data.questions)
        setCorrectAnswers(data.questions.map((q) => q.correctAnswer))
        setUserAnswers(new Array(data.questions.length).fill(null)) // dynamically set user answers
        toast.success(`Generated ${data.questions.length} questions for ${topic}!`)
      } else {
        toast.error(data.message || "Failed to generate questions.")
      }
    } catch (error) {
      console.error("Error fetching questions:", error)
      toast.error("Error fetching questions. Please try again.")
    }

    setLoading(false)
  }

  const handleAnswer = (index, answer) => {
    const updatedAnswers = [...userAnswers]
    updatedAnswers[index] = answer
    setUserAnswers(updatedAnswers)
  }

  const submitTest = async () => {
    if (userAnswers.includes(null)) {
      toast.error("Please answer all questions.")
      return
    }

    setSubmitting(true)
    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/submit", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, topic, userAnswers, correctAnswers }),
      })
      const data = await response.json()

      if (data.success) {
        setResult(data)
        toast.success("Test submitted successfully!")
      } else {
        toast.error(data.message || "Failed to submit test.")
      }
    } catch (error) {
      console.error("Error submitting test:", error)
      toast.error("Error submitting test. Please try again.")
    }
    setSubmitting(false)
  }

  const resetQuiz = () => {
    setTopic("")
    setQuestions([])
    setCorrectAnswers([])
    setUserAnswers([])
    setResult(null)
    setLoading(false)
    setSubmitting(false)
  }

  const answeredCount = userAnswers.filter((answer) => answer !== null).length
  const totalQuestions = questions.length

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-blue-50 via-white to-indigo-50 dark:from-gray-900 dark:to-gray-800">
      <Navbar />
      <div className="flex-grow container mx-auto px-4 py-8 max-w-4xl">
        <div className="text-center mb-10">
          <h1 className="text-4xl font-extrabold text-gray-900 dark:text-white leading-tight">
            Knowledge <span className="text-blue-600 dark:text-blue-400">Challenge</span>
          </h1>
          <p className="mt-3 text-lg text-gray-600 dark:text-gray-300">
            Test your skills and earn badges with our interactive quizzes
          </p>
        </div>

        {/* Topic Selection */}
        {!questions.length && !result && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 sm:p-8 mb-8 border border-gray-100 dark:border-gray-700">
            <h2 className="text-2xl font-bold mb-6 text-gray-900 dark:text-white flex items-center">
              <BookOpen className="h-7 w-7 mr-3 text-blue-500 dark:text-blue-400" />
              Select Your Topic
            </h2>
            <div className="relative mb-6">
              <select
                className="w-full p-4 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-800 dark:text-white"
                value={topic}
                onChange={(e) => setTopic(e.target.value)}
                disabled={loading}
              >
                <option value="">-- Select a Topic --</option>
                {predefinedTopics.map((t) => (
                  <option key={t} value={t}>
                    {t}
                  </option>
                ))}
              </select>
              <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-4 text-gray-700 dark:text-gray-300">
                <ChevronDown className="h-5 w-5" />
              </div>
            </div>

            <button
              onClick={fetchQuestions}
              disabled={loading || !topic}
              className={`w-full py-4 rounded-lg font-bold text-white text-lg ${
                loading
                  ? "bg-blue-500 cursor-wait"
                  : !topic
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-blue-600 hover:bg-blue-700 shadow-md"
              }`}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <Loader2 className="animate-spin -ml-1 mr-3 h-5 w-5" />
                  Generating...
                </span>
              ) : (
                "Start Quiz"
              )}
            </button>
          </div>
        )}

        {/* Questions Section */}
        {questions.length > 0 && !result && (
          <div className="space-y-6 mb-8">
            <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 border border-gray-100 dark:border-gray-700">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                  Quiz on <span className="text-blue-600">{topic}</span>
                </h2>
                <div className="text-lg font-medium text-gray-700 dark:text-gray-300">
                  {answeredCount}/{totalQuestions} Answered
                </div>
              </div>

              {questions.map((q, i) => (
                <div key={i} className="mb-4">
                  <h3 className="text-lg font-semibold text-gray-800 dark:text-white mb-2">
                    {i + 1}. {q.question}
                  </h3>
                  <div className="space-y-2">
                    {q.options.map((option, idx) => (
                      <label
                        key={idx}
                        className={`flex items-center p-3 rounded-lg cursor-pointer border ${
                          userAnswers[i] === option
                            ? "bg-blue-100 border-blue-400 dark:bg-blue-900"
                            : "border-gray-200 dark:border-gray-700"
                        }`}
                      >
                        <input
                          type="radio"
                          name={`q${i}`}
                          value={option}
                          checked={userAnswers[i] === option}
                          onChange={() => handleAnswer(i, option)}
                          className="sr-only"
                        />
                        <span className="text-gray-800 dark:text-gray-200">{option}</span>
                      </label>
                    ))}
                  </div>
                </div>
              ))}

              <button
                onClick={submitTest}
                disabled={submitting || userAnswers.includes(null)}
                className={`mt-6 w-full py-4 rounded-lg font-bold text-white ${
                  userAnswers.includes(null)
                    ? "bg-gray-400 cursor-not-allowed"
                    : submitting
                    ? "bg-blue-500 cursor-wait"
                    : "bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
                }`}
              >
                {submitting ? (
                  <span className="flex items-center justify-center">
                    <Loader2 className="animate-spin -ml-1 mr-3 h-5 w-5" />
                    Submitting...
                  </span>
                ) : (
                  "Submit Quiz"
                )}
              </button>
            </div>
          </div>
        )}

        {/* Result Section */}
        {result && (
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 border border-purple-300 dark:border-purple-700">
            <div className="text-center">
              <div className="inline-block p-4 bg-purple-100 dark:bg-purple-900 rounded-full mb-4">
                <CheckCircle className="h-10 w-10 text-purple-600 dark:text-purple-400" />
              </div>
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Quiz Completed!</h2>
              <div className="text-4xl font-extrabold text-purple-600 dark:text-purple-400 my-4">
                {result.correctCount}/{totalQuestions}
              </div>
              <p className="text-lg text-gray-600 dark:text-gray-300">
                {result.correctCount === totalQuestions
                  ? "Perfect score! Excellent work!"
                  : result.correctCount >= totalQuestions / 2
                  ? "Good job! You're making progress."
                  : "Keep practicing to improve your score."}
              </p>

              {result.badgeEarned && (
                <div className="mt-6 p-4 bg-gradient-to-r from-yellow-100 to-amber-100 dark:from-yellow-900 dark:to-amber-900 rounded-lg">
                  <div className="flex items-center justify-center">
                    <Award className="h-6 w-6 text-amber-600 dark:text-amber-400 mr-2" />
                    <span className="text-lg font-bold text-amber-800 dark:text-amber-300">
                      Badge Earned: {result.badgeEarned}
                    </span>
                  </div>
                </div>
              )}

              <button
                onClick={resetQuiz}
                className="mt-6 px-6 py-3 bg-purple-600 hover:bg-purple-700 dark:bg-purple-700 dark:hover:bg-purple-800 text-white font-semibold rounded-lg"
              >
                Take Another Quiz
              </button>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  )
}

export default MCQTest
