"use client"

import { useState } from "react"
import { useSelector } from "react-redux"
import Navbar from "./Navbar"
import Footer from "./Footer"
import { toast } from "react-toastify"

const MCQTest = () => {
  const [topic, setTopic] = useState("")
  const [questions, setQuestions] = useState([])
  const [correctAnswers, setCorrectAnswers] = useState([])
  const [userAnswers, setUserAnswers] = useState([])
  const [result, setResult] = useState(null)
  const [loading, setLoading] = useState(false)
  const [submitting, setSubmitting] = useState(false)
  const userId = useSelector((state) => state.auth.userId) // Assuming userId is available in Redux

  const predefinedTopics = ["JavaScript", "Python", "React.js", "Node.js", "MongoDB", "Data Structures"]

  const fetchQuestions = async () => {
    if (!topic) {
      toast.error("Please select a topic.")
      return
    }

    setLoading(true)
    setQuestions([]) // Clear previous questions
    setUserAnswers([]) // Clear previous answers
    setResult(null) // Clear previous results

    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/generate", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ topic }),
      })
      const data = await response.json()

      if (data.success) {
        setQuestions(data.questions)
        setCorrectAnswers(data.questions.map((q) => q.correctAnswer))
        setUserAnswers(new Array(data.questions.length).fill(null)) // Initialize with null for unanswered
        toast.success(`Generated ${data.questions.length} questions for ${topic}!`)
      } else {
        toast.error(data.message || "Failed to generate questions.")
      }
    } catch (error) {
      console.error("Error fetching questions:", error)
      toast.error("Error fetching questions. Please try again.")
    }
    setLoading(false)
  }

  const handleAnswer = (index, answer) => {
    const updatedAnswers = [...userAnswers]
    updatedAnswers[index] = answer
    setUserAnswers(updatedAnswers)
  }

  const submitTest = async () => {
    if (userAnswers.includes(null)) {
      toast.error("Please answer all questions before submitting.")
      return
    }

    setSubmitting(true)
    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/submit", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, topic, userAnswers, correctAnswers }),
      })
      const data = await response.json()

      if (data.success) {
        setResult(data)
        toast.success("Test submitted successfully!")
      } else {
        toast.error(data.message || "Failed to submit test.")
      }
    } catch (error) {
      console.error("Error submitting test:", error)
      toast.error("Error submitting test. Please try again.")
    }
    setSubmitting(false)
  }

  const resetQuiz = () => {
    setTopic("")
    setQuestions([])
    setCorrectAnswers([])
    setUserAnswers([])
    setResult(null)
    setLoading(false)
    setSubmitting(false)
  }

  const answeredCount = userAnswers.filter((answer) => answer !== null).length
  const totalQuestions = questions.length

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-br from-gray-50 via-white to-indigo-50">
      <Navbar />

      <div className="flex-grow container mx-auto px-4 py-8 max-w-4xl">
        <div className="text-center mb-10">
          <h1 className="text-4xl sm:text-5xl font-extrabold text-gray-900 leading-tight">
            Knowledge <span className="text-gray-600">Challenge</span>
          </h1>
          <p className="mt-3 text-lg text-gray-600">Test your skills and earn badges with our interactive quizzes</p>
        </div>

        {/* Topic Selection Card */}
        {!questions.length && !result && (
          <div className="bg-white rounded-xl shadow-lg p-6 sm:p-8 mb-8 transform transition-all hover:shadow-xl border border-gray-100">
            <h2 className="text-2xl font-bold mb-6 text-gray-900 flex items-center">
              <svg className="h-7 w-7 mr-3 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"
                />
              </svg>
              Select Your Topic
            </h2>
            <div className="relative mb-6">
              <select
                className="w-full p-3 sm:p-4 border border-gray-300 rounded-lg bg-gray-50 text-gray-800 appearance-none focus:outline-none focus:ring-2 focus:ring-gray-500 transition-all text-base sm:text-lg"
                value={topic}
                onChange={(e) => setTopic(e.target.value)}
                disabled={loading}
              >
                <option value="">-- Select a Topic --</option>
                {predefinedTopics.map((t) => (
                  <option key={t} value={t}>
                    {t}
                  </option>
                ))}
              </select>
              <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-4 text-gray-700">
                <svg className="fill-current h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                  <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />
                </svg>
              </div>
            </div>
            <button
              onClick={fetchQuestions}
              disabled={loading || !topic}
              className={`w-full py-3 sm:py-4 rounded-lg font-semibold text-white text-lg transition-all transform hover:scale-[1.01] active:scale-[0.99] ${
                !topic
                  ? "bg-gray-400 cursor-not-allowed"
                  : loading
                    ? "bg-gray-500 cursor-wait"
                    : "bg-gray-600 hover:bg-gray-700 shadow-md hover:shadow-lg"
              }`}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" fill="none" viewBox="0 0 24 24">
                    <circle
                      className="opacity-25"
                      cx="12"
                      cy="12"
                      r="10"
                      stroke="currentColor"
                      strokeWidth="4"
                    ></circle>
                    <path
                      className="opacity-75"
                      fill="currentColor"
                      d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                    ></path>
                  </svg>
                  Generating Questions...
                </span>
              ) : (
                "Start Quiz"
              )}
            </button>
          </div>
        )}

        {/* Questions Section */}
        {questions.length > 0 && !result && (
          <div className="space-y-6 mb-8">
            <div className="bg-white rounded-xl shadow-lg p-6 sm:p-8 border border-gray-100">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-gray-900">
                  Quiz on <span className="text-gray-600">{topic}</span>
                </h2>
                <div className="text-lg font-semibold text-gray-700">
                  {answeredCount}/{totalQuestions} Answered
                </div>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2.5 mb-6">
                <div
                  className="bg-gray-600 h-2.5 rounded-full transition-all duration-300"
                  style={{ width: `${(answeredCount / totalQuestions) * 100}%` }}
                ></div>
              </div>

              {questions.map((q, i) => (
                <div key={i} className="bg-gray-50 rounded-xl shadow-sm p-5 mb-4 border border-gray-100 last:mb-0">
                  <div className="flex items-start mb-4">
                    <span className="flex-shrink-0 bg-gray-100 text-gray-700 font-bold rounded-full w-8 h-8 flex items-center justify-center mr-3 text-sm">
                      {i + 1}
                    </span>
                    <h3 className="text-lg font-semibold text-gray-800 leading-relaxed">{q.question}</h3>
                  </div>
                  <div className="ml-11 space-y-3">
                    {q.options.map((option, idx) => (
                      <label
                        key={idx}
                        className={`flex items-center p-3 rounded-lg cursor-pointer transition-all duration-200 ${
                          userAnswers[i] === option
                            ? "bg-gray-100 border-2 border-gray-500 text-gray-800 shadow-sm"
                            : "border border-gray-200 hover:bg-gray-100 text-gray-700"
                        }`}
                      >
                        <div
                          className={`w-5 h-5 flex-shrink-0 rounded-full border-2 ${
                            userAnswers[i] === option ? "border-gray-500 bg-gray-500" : "border-gray-400 bg-white"
                          } flex items-center justify-center mr-3 transition-all`}
                        >
                          {userAnswers[i] === option && <div className="w-2.5 h-2.5 rounded-full bg-white"></div>}
                        </div>
                        <span className="text-base">{option}</span>
                        <input
                          type="radio"
                          name={`q${i}`}
                          value={option}
                          onChange={() => handleAnswer(i, option)}
                          checked={userAnswers[i] === option}
                          className="sr-only"
                        />
                      </label>
                    ))}
                  </div>
                </div>
              ))}
            </div>

            <button
              onClick={submitTest}
              disabled={submitting || userAnswers.includes(null)}
              className={`w-full py-4 rounded-xl text-white text-lg font-bold transition-all transform hover:scale-[1.01] active:scale-[0.99] ${
                userAnswers.includes(null)
                  ? "bg-gray-400 cursor-not-allowed"
                  : submitting
                    ? "bg-gray-500 cursor-wait"
                    : "bg-gradient-to-r from-gray-600 to-purple-600 hover:from-gray-700 hover:to-purple-700 shadow-lg hover:shadow-xl"
              }`}
            >
              {submitting ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" fill="none" viewBox="0 0 24 24">
                    <circle
                      className="opacity-25"
                      cx="12"
                      cy="12"
                      r="10"
                      stroke="currentColor"
                      strokeWidth="4"
                    ></circle>
                    <path
                      className="opacity-75"
                      fill="currentColor"
                      d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                    ></path>
                  </svg>
                  Submitting...
                </span>
              ) : (
                "Submit Quiz"
              )}
            </button>
            {userAnswers.includes(null) && (
              <p className="mt-3 text-center text-sm text-gray-600">
                Please answer all questions ({totalQuestions - answeredCount} remaining)
              </p>
            )}
          </div>
        )}

        {/* Result Display */}
        {result && (
          <div className="bg-white rounded-xl shadow-lg p-6 sm:p-8 border-2 border-purple-300 animate-fade-in">
            <div className="text-center">
              <div className="inline-block p-4 bg-purple-100 rounded-full mb-4">
                <svg className="h-12 w-12 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                </svg>
              </div>
              <h2 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-2">Quiz Completed!</h2>
              <div className="text-5xl sm:text-6xl font-extrabold text-purple-600 my-4">
                {result.correctCount}/{questions.length}
              </div>
              <p className="text-lg text-gray-600 mb-4">
                {result.correctCount === questions.length
                  ? "Perfect score! Excellent work!"
                  : result.correctCount >= questions.length / 2
                    ? "Good job! You're making progress."
                    : "Keep practicing to improve your score."}
              </p>
              {result.badgeEarned && (
                <div className="mt-6 p-4 bg-gradient-to-r from-yellow-100 to-amber-100 rounded-lg inline-block">
                  <div className="flex items-center justify-center">
                    <svg className="h-8 w-8 text-amber-600 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M5 3v4M3 5h4M6 17v4m-2-2h4m5-16l2.286 6.857L21 12l-5.714 2.143L13 21l-2.286-6.857L5 12l5.714-2.143L13 3z"
                      />
                    </svg>
                    <span className="text-xl font-bold text-amber-800">Badge Earned: {result.badgeEarned}</span>
                  </div>
                </div>
              )}
              <button
                onClick={resetQuiz}
                className="mt-8 px-8 py-4 bg-purple-600 hover:bg-purple-700 text-white font-semibold rounded-xl transition-all transform hover:scale-[1.01] active:scale-[0.99] shadow-md hover:shadow-lg"
              >
                Take Another Quiz
              </button>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  )
}

export default MCQTest
