import { useState } from "react"
import { useSelector } from "react-redux"
import Navbar from "./Navbar"
import Footer from "./Footer"
import { toast } from "react-toastify"

const MCQTest = () => {
  const [topic, setTopic] = useState("")
  const [questions, setQuestions] = useState([])
  const [correctAnswers, setCorrectAnswers] = useState([])
  const [userAnswers, setUserAnswers] = useState([])
  const [result, setResult] = useState(null)
  const [loading, setLoading] = useState(false)

  const userId = useSelector((state) => state.auth.userId)
  const predefinedTopics = ["JavaScript", "Python", "React.js", "Node.js", "MongoDB", "Data Structures"]

  const fetchQuestions = async () => {
    if (!topic) return toast.error("Please select a topic.")
    setLoading(true)
    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/generate", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ topic }),
      })

      const data = await response.json()
      setQuestions(data.questions)
      setCorrectAnswers(data.questions.map((q) => q.correctAnswer))
      setUserAnswers(new Array(5).fill(null))
      setResult(null)
    } catch (error) {
      console.error("Error fetching questions:", error)
    }
    setLoading(false)
  }

  const handleAnswer = (index, answer) => {
    const updatedAnswers = [...userAnswers]
    updatedAnswers[index] = answer
    setUserAnswers(updatedAnswers)
  }

  const submitTest = async () => {
    if (userAnswers.includes(null)) return toast.error("Please answer all questions.")
    setLoading(true)
    try {
      const response = await fetch("https://interview-platform-backend-xp3r.onrender.com/api/v1/mcq/submit", {
        credentials: "include",
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, topic, userAnswers, correctAnswers }),
      })

      const data = await response.json()
      setResult(data)
    } catch (error) {
      console.error("Error submitting test:", error)
    }
    setLoading(false)
  }

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 dark:from-gray-900 dark:to-gray-800">
      <Navbar />
      <main className="flex-grow container mx-auto px-4 py-10 max-w-4xl">
        <header className="text-center mb-12">
          <h1 className="text-5xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-purple-600 to-indigo-600">
            Knowledge Challenge
          </h1>
          <p className="mt-3 text-lg text-gray-600 dark:text-gray-300">
            Test your skills and earn badges with our interactive quizzes
          </p>
        </header>

        {/* Topic Card */}
        <section className="bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6 border dark:border-gray-700 mb-10">
          <h2 className="text-2xl font-semibold text-gray-800 dark:text-white mb-4">
            🧠 Select Your Topic
          </h2>
          <div className="relative">
            <select
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
              className="w-full p-3 pr-10 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white border border-gray-300 dark:border-gray-600 focus:ring-2 focus:ring-purple-500 transition"
            >
              <option value="">-- Select a Topic --</option>
              {predefinedTopics.map((t) => (
                <option key={t} value={t}>
                  {t}
                </option>
              ))}
            </select>
            <div className="absolute right-3 top-3 text-gray-600 dark:text-gray-300 pointer-events-none">
              ▼
            </div>
          </div>
          <button
            onClick={fetchQuestions}
            disabled={loading || !topic}
            className={`mt-5 w-full py-3 rounded-lg font-semibold text-white transition duration-300 ${
              !topic
                ? "bg-gray-400 cursor-not-allowed"
                : loading
                ? "bg-gray-500 cursor-wait"
                : "bg-purple-600 hover:bg-purple-700"
            }`}
          >
            {loading ? "Generating Questions..." : "Start Quiz"}
          </button>
        </section>

        {/* Questions */}
        {questions.length > 0 && (
          <section className="space-y-6 mb-10 animate-fade-in">
            {questions.map((q, i) => (
              <div
                key={i}
                className="bg-white dark:bg-gray-800 rounded-xl shadow p-6 border dark:border-gray-700"
              >
                <h3 className="text-lg font-medium text-gray-800 dark:text-white mb-4">
                  {i + 1}. {q.question}
                </h3>
                <div className="space-y-2">
                  {q.options.map((option, idx) => (
                    <label
                      key={idx}
                      className={`block px-4 py-2 rounded-lg border flex items-center transition ${
                        userAnswers[i] === option
                          ? "bg-purple-100 dark:bg-purple-900 border-purple-400"
                          : "bg-gray-50 dark:bg-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 border-gray-300 dark:border-gray-600"
                      }`}
                    >
                      <input
                        type="radio"
                        name={`q${i}`}
                        value={option}
                        onChange={() => handleAnswer(i, option)}
                        checked={userAnswers[i] === option}
                        className="mr-3"
                      />
                      <span className="text-gray-800 dark:text-gray-200">{option}</span>
                    </label>
                  ))}
                </div>
              </div>
            ))}
            <button
              onClick={submitTest}
              disabled={loading || result !== null || userAnswers.includes(null)}
              className={`w-full py-4 rounded-xl text-white text-lg font-bold transition duration-300 ${
                userAnswers.includes(null)
                  ? "bg-gray-400 cursor-not-allowed"
                  : loading
                  ? "bg-green-500 cursor-wait"
                  : result !== null
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-green-600 hover:bg-green-700 shadow-md"
              }`}
            >
              {loading
                ? "Submitting..."
                : userAnswers.includes(null)
                ? "Answer All Questions to Submit"
                : result !== null
                ? "Test Submitted"
                : "Submit Answers"}
            </button>
          </section>
        )}

        {/* Result */}
        {result && (
          <section className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-8 border-2 border-yellow-300 dark:border-yellow-600 text-center animate-fade-in">
            <div className="mb-6">
              <div className="inline-block bg-yellow-100 dark:bg-yellow-900 p-4 rounded-full">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-10 w-10 text-yellow-500"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
            </div>
            <h2 className="text-3xl font-bold text-gray-800 dark:text-white">Quiz Completed!</h2>
            <div className="my-6 text-5xl font-extrabold text-yellow-600 dark:text-yellow-400">
              {result.correctCount}/5
            </div>
            <p className="text-gray-600 dark:text-gray-300 mb-6">
              {result.correctCount === 5
                ? "Perfect score! Excellent work!"
                : result.correctCount >= 3
                ? "Good job! You're making progress."
                : "Keep practicing to improve your score."}
            </p>
            {result.badgeEarned && (
              <div className="inline-flex items-center bg-amber-100 dark:bg-amber-900 px-6 py-3 rounded-xl shadow-sm">
                <span className="text-amber-800 dark:text-amber-300 font-semibold text-lg">
                  🏅 Badge Earned: {result.badgeEarned}
                </span>
              </div>
            )}
            <div className="mt-8">
              <button
                onClick={() => {
                  setQuestions([])
                  setUserAnswers([])
                  setResult(null)
                  setTopic("")
                }}
                className="px-6 py-3 bg-purple-600 hover:bg-purple-700 text-white font-medium rounded-lg transition"
              >
                Take Another Quiz
              </button>
            </div>
          </section>
        )}
      </main>
      <Footer />
    </div>
  )
}

export default MCQTest
